import React from 'react';
import { TrendingUp, TrendingDown } from 'lucide-react';

const StatusCard = ({ title, value, icon: Icon, color, trend, subtitle, onClick }) => {
  const getColorClasses = (color) => {
    switch(color) {
      case 'blue':
        return {
          border: 'border-l-primary-500',
          icon: 'text-primary-500',
          bg: 'bg-primary-50'
        };
      case 'green':
        return {
          border: 'border-l-success-500',
          icon: 'text-success-500',
          bg: 'bg-success-50'
        };
      case 'red':
        return {
          border: 'border-l-danger-500',
          icon: 'text-danger-500',
          bg: 'bg-danger-50'
        };
      case 'orange':
        return {
          border: 'border-l-warning-500',
          icon: 'text-warning-500',
          bg: 'bg-warning-50'
        };
      case 'purple':
        return {
          border: 'border-l-purple-500',
          icon: 'text-purple-500',
          bg: 'bg-purple-50'
        };
      default:
        return {
          border: 'border-l-gray-500',
          icon: 'text-gray-500',
          bg: 'bg-gray-50'
        };
    }
  };

  const colorClasses = getColorClasses(color);

  return (
    <div 
      className={`bg-white rounded-lg shadow-md p-4 border-l-4 ${colorClasses.border} ${onClick ? 'cursor-pointer hover:shadow-lg transition-shadow' : ''}`}
      onClick={onClick}
    >
      <div className="flex items-center justify-between">
        <div className="flex-1">
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900 mt-1">{value}</p>
          {subtitle && (
            <div className="flex items-center mt-2">
              {trend && (
                <div className={`flex items-center mr-2 ${
                  trend === 'up' ? 'text-danger-600' : 'text-success-600'
                }`}>
                  {trend === 'up' ? (
                    <TrendingUp className="w-3 h-3 mr-1" />
                  ) : (
                    <TrendingDown className="w-3 h-3 mr-1" />
                  )}
                </div>
              )}
              <p className="text-xs text-gray-500">{subtitle}</p>
            </div>
          )}
        </div>
        <div className={`p-3 rounded-full ${colorClasses.bg}`}>
          <Icon className={`h-6 w-6 ${colorClasses.icon}`} />
        </div>
      </div>
    </div>
  );
};

export default StatusCard;